create table tb_cesta (cod int8, quantidade int4, total float8, produto_id int8 not null, cliente_id int8 not null, primary key (cliente_id, produto_id));
create table tb_cliente (id int8 generated by default as identity, bairro varchar(255), cep varchar(255), cidade varchar(255), email varchar(255), estado varchar(255), logradouro varchar(255), nome varchar(255), numero_logradouro varchar(255), senha varchar(255), telefone varchar(255), primary key (id));
create table tb_contato (id int8 generated by default as identity, email varchar(255), mensagem varchar(255), nome varchar(255), primary key (id));
create table tb_produto (produtoid int8 generated by default as identity, foto varchar(255), nome varchar(255), preco float8, quantidade int4, primary key (produtoid));
create table tb_vendedor (id int8 generated by default as identity, bairro varchar(255), cep varchar(255), cidade varchar(255), email varchar(255), estado varchar(255), logradouro varchar(255), nome varchar(255), numero_logradouro varchar(255), senha varchar(255), telefone varchar(255), primary key (id));
alter table if exists tb_cesta add constraint FKf9xoty7v36x1kdk8r765uxh6b foreign key (produto_id) references tb_produto;
alter table if exists tb_cesta add constraint FKlwjmn0rvqx0tayxgb2ok2gg4v foreign key (cliente_id) references tb_cliente;
